mapper.select:

from tb_scd_apply
where 1=1


        and apply_no=#{applyNo}




        and apply_emp=(SELECT id from tb_scd_user where real_name=#{applyEmpName})



    and  apply_emp=#{applyEmpId}


        and status=#{status}


    and deal_emp=#{dealEmpId}


    <apply_time=<#{applyTime}

mapper.update:
UPDATE tb_scd_apply SET

      status=#{status}


      deal_emp=#{dealEmpId}


      back_time=#{backTime}


      deal_time=#{dealTime}


      remark=#{remark}


      deal_text=#{dealText}


      apply_text=#{applyText}


      modify_emp=#{modifyEmp}

  modify_time=now()
  WHERE id=#{id}
